		$ChecklistText       = $ErrorCodeList{$SubCodeMatches}{checklist} ; 
		$CorrectionText      = $ErrorCodeList{$SubCodeMatches}{correction}; 
	}
	
	return($ClassDescription, $ClassWorkaround, $ReasonText, $ChecklistText, $CorrectionText);
